INSERT INTO `listStandard1` VALUES (7, '[GB 2626-2019] 呼吸防护 自吸过滤式防颗粒物呼吸器', '即将实施', '本标准规定了自吸过滤式防颗粒物呼吸器的分类和标记、技术要求、检测方法和标识。 本标准适用于防护颗粒物的自吸过滤式呼吸器。 本标准不适用于防护有害气体和蒸气的呼吸器，不适用于缺氧环境、水下作业、逃生和消防用呼吸器。', '2020-07-01', '国家市场监督管理总局  国家标准化管理委员会', '3907', '防护口罩相关标准', '3899', '1', '3', '891');
INSERT INTO `listStandard1` VALUES (8, '[YY/T 1497-2016] 医用防护口罩材料病毒过滤效率评价测试方法 Phi-X174噬菌体测试方法', '现行', '本标准规定了用Phi-X174噬菌体悬浮液为替代微生物，对医用防护口罩或口罩材料进行病毒过滤效率的测试方法。本标准适用于有病毒过滤效率评价要求的医用防护口罩或口罩材料。', '2017-06-01', '国家食品药品监督管理总局', '3907', '防护口罩相关标准', '3899', '1', '3', '891');
INSERT INTO `listStandard1` VALUES (9, '[YY/T 0969-2013] 一次性使用医用口罩', '现行', '本标准规定了一次性使用医用口罩的技术要求、试验方法、标志、使用说明书、包装、运输和贮存。本标准适用于覆盖使用者的口、鼻及下颌，用于普通医疗环境中佩戴、阻隔口腔和鼻腔呼出或喷出污染物的一次性使用口罩。本标准不适用于医用防护口罩、医用外科口罩。', '2014-10-01', '国家食品药品监督管理局', '3907', '防护口罩相关标准', '3899', '1', '3', '891');
INSERT INTO `listStandard1` VALUES (10, '[YY/T 0866-2011] 医用防护口罩总泄漏率测试方法', '现行', '', '2013-06-01', '国家食品药品监督管理局', '3907', '防护口罩相关标准', '3899', '1', '3', '891');
INSERT INTO `listStandard1` VALUES (11, '[YY 0469-2011] 医用外科口罩', '现行', '本标准规定了医用外科口罩(以下简称口罩)的技术要求、试验方法、标志与使用说明及包装、运输和贮存。本标准适用于由临床医务人员在有创操作等过程中所佩带的一次性口罩。', '2013-06-01', '国家食品药品监督管理局', '3907', '防护口罩相关标准', '3899', '1', '3', '891');
INSERT INTO `listStandard1` VALUES (12, '[GB/T 32610-2016] 日常防护型口罩技术规范', '现行', '本标准规定了日常防护型口罩的术语与定义、分级、技术要求、测试方法、检验规则、包装、标识及储运要求。本标准适用于在日常生活中空气污染环境下滤除颗粒物所佩戴的防护型口罩。? 本标准不适用于缺氧环境、水下作业、逃生、消防、医用及工业防尘等特殊行业用呼吸防护用品也不适用于婴幼儿、儿童呼吸防护用品。基本信息', '2016-11-01', '中华人民共和国国家质量监督检验检疫总局  中国国家标准化管理委员会', '3907', '防护口罩相关标准', '3899', '1', '3', '891');
INSERT INTO `listStandard1` VALUES (13, '[GB 2626-2006] 呼吸防护用品——自吸过滤式防颗粒物呼吸器', '现行', '本标准规定了自吸过滤式防颗粒物呼吸器的技术要求、检测方法和标识。', '2006-12-01', '国家安全生产监督管理局', '3907', '防护口罩相关标准', '3899', '1', '3', '891');
INSERT INTO `listStandard1` VALUES (14, '[GB 19083-2010] 医用防护口罩技术要求', '现行', '本标准规定了医用防护口罩的技术要求、试验方法、标志与使用说明及包装、运输和贮存。 本标准适用于医疗工作环境下，过滤空气中的颗粒物，阻隔飞沫、血液、体液、分泌物等的自吸过滤式医用防护口罩。', '2011-08-01', '中华人民共和国国家质量监督检验检疫总局  中国国家标准化管理委员会', '3907', '防护口罩相关标准', '3899', '1', '3', '891');
INSERT INTO `listStandard1` VALUES (15, '[FZ/T 73049-2014] 针织口罩', '现行', '本标准规定了针织口罩的规格、要求、试验、判定规则、产品使用说明、包装、运输和贮存。本标准适用于鉴定以针织面料为主要材料制成的口罩的品质。本标准不适用于医用口罩和劳动防护口罩。本标准不适用于年龄在36个月及以下的婴幼儿服饰。', '2015-04-01', '中华人民共和国工业和信息化部', '3907', '防护口罩相关标准', '3899', '1', '3', '891');
INSERT INTO `listStandard1` VALUES (16, '[DB51/T 1797-2014] 普通防护口罩通用技术条件', '现行', '地方标准《普通防护口罩通用技术条件》由四川省产品质量监督检验检测院归口上报，主管部门为四川省质量技术监督局。主要起草单位四川省产品质量监督检验检测院、绵阳市华信劳保用品制造有限公司、绵阳市宣铭劳保用品有限公司。主要起草人董平 、陈永忠 、胡沛 、李波 。', '2015-03-01', '四川省质量技术监督局', '3907', '防护口罩相关标准', '3899', '1', '3', '891');
INSERT INTO `listStandard1` VALUES (17, '[DB14/T 730-2013] 普通口罩', '现行', '地方标准《普通口罩》，主管部门为山西省质量技术监督局。', '2013-06-20', '山西省质量技术监督局', '3907', '防护口罩相关标准', '3899', '1', '3', '891');
INSERT INTO `listStandard1` VALUES (18, '[YY/T 0506.8-2019] 病人、医护人员和器械用手术单、手术衣和洁净服 第8部分：产品专用要求', '即将实施', 'YY/T 0506的本部分规定了病人、医护人员和器械用手术单、手术衣和洁净服的专用要求。本部分适用于对净化程度要求较高、需要病人全身覆盖的手术用手术单、手术衣和洁净服。注： 本部分规定的手术单不包括创面开放小、手术净化级别要求低的小手术用一次性使用及重复性使用手术单。手术室用手术单、手术衣和洁净服的临床要求取决于手术创面的开放程度和洁净手术室的净化级别。', '2020-08-01', '国家药品监督管理局', '3908', '防护服相关标准', '3899', '1', '3', '892');
INSERT INTO `listStandard1` VALUES (19, '[YY/T 1632-2018] 医用防护服材料的阻水性：冲击穿透测试方法', '现行', '本标准规定了医用防护服材料阻水性冲击穿透测试方法的原理、操作步骤、结果评价与实验报告等。本标准适用于经过或未经过防水整理的医用防护服材料阻水性评价。本标准不适用于拉伸状态下不能保持平整状态的医用防护服材料。', '2019-06-01', '国家药品监督管理局', '3908', '防护服相关标准', '3899', '1', '3', '892');
INSERT INTO `listStandard1` VALUES (20, '[YY/T 1499-2016] 医用防护服的液体阻隔性能和分级', '现行', '本标准规定了医用防护服液体阻隔性能的分级方法和相关的标识要求。本标准适用于标示有液体阻隔性能或液生微生物阻隔性能的防护服。', '2017-06-01', '国家食品药品监督管理总局', '3908', '防护服相关标准', '3899', '1', '3', '892');
INSERT INTO `listStandard1` VALUES (21, '[YY/T 1498-2016] 医用防护服的选用评估指南', '现行', '本标准给出了关于防护服材料的类型、安全和性能指标、防护服产品的评价和选择、根据特定的医护程序选择防护等级的指导原则以及防护服的维护和处理的指南。 本标准不可能涵盖医疗机构在选择防护服产品时所必须的所有技术信息也不宜作为医用防护服产品的评价标准。', '2017-06-01', '国家食品药品监督管理总局', '3908', '防护服相关标准', '3899', '1', '3', '892');
INSERT INTO `listStandard1` VALUES (22, '[YY/T 1425-2016] 防护服材料抗注射针穿刺性能标准试验方法', '现行', '本标准规定了防护服材料抗注射针穿刺性能的试验条件、试验程序及结果报告。本标准适用于医用防护服注射针穿刺阻力的评价。', '2017-01-01', '国家食品药品监督管理总局', '3908', '防护服相关标准', '3899', '1', '3', '892');
INSERT INTO `listStandard1` VALUES (23, '[YY/T 0699-2008] 液态化学品防护装备 防护服材料抗加压液体穿透性能测试方法', '现行', '本标准规定了用于实验室测定一定压力下持续接触液体，如防护服反复暴露在受压的泼溅和喷洒的液体中，防护服材料抗液体穿透能力的试验方法。防护服“合格／不合格”的结论以目测液体穿透情况确定。本试验方法通常用于评价防护服材料和从防护服成品上取样材料的防护效果。', '2010-01-01', '中华人民共和国国家质量监督检验检疫总局  中国国家标准化管理委员会', '3908', '防护服相关标准', '3899', '1', '3', '892');
INSERT INTO `listStandard1` VALUES (24, '[YY/T 0691-2008] 传染性病原体防护装备 医用面罩抗合成血穿透性试验方法（固定体积、水平喷射）', '现行', '本标准给出了检测医用面罩防合成血喷溅穿透性能的实验室试验方法。本标准主要用于评价制作医用面罩的材料或某些结构的性能。本标准不评价医用面罩的设计、结构、界面或其他能影响医用面罩总体保护能力（如过滤效率和压降）的因素。本试验不评价医用面罩的透气性或其他影响医用面罩呼吸舒适性的性能。本试验评估医用面罩的防护性能。本试验不评估医用面罩对经空气传播，或沉积在医用面罩表面体液形成气溶胶穿透传播的防护能力。', '2010-01-01', '国家食品药品监督管理局', '3908', '防护服相关标准', '3899', '1', '3', '892');
INSERT INTO `listStandard1` VALUES (25, '[YY/T 0689-2008] 血液和体液防护装备 防护服材料抗血液传播病原体穿透性能测试Phi-X174噬菌体试验方法', '现行', '本标准规定了测定防护服材料抗血液传播病原体穿透能力的实验室试验方法。本试验方法使用了一种含替代微生物的悬浮液。防护服测试合格/不合格是运用YY0699规定的试验仪器在一特定的流体静力学压力下测定病毒穿透能力。本试验方法对较厚、有衬里易吸收试验液体的防护服材料可能无效。本试验方法中某些试验步骤灵敏度较高。由于本方法对完成时间有要求,因此本方法不适宜作为防护服或防护服材料质量控制或保证程序。', '2010-01-01', '国家食品药品监督管理局', '3908', '防护服相关标准', '3899', '1', '3', '892');
INSERT INTO `listStandard1` VALUES (26, '[YY/T 0506.7-2014] 病人、医护人员和器械用手术单、手术衣和洁净服 第7部分：洁净度-微生物试验方法', '现行', '本标准适用于重复性使用手术单、手术衣和洁净服和原材料的微生物负载评价。本标准规定了手术单、手术衣和洁净服产品上微生物的评价的试验方法。', '2015-07-01', '国家食品药品监督管理总局', '3908', '防护服相关标准', '3899', '1', '3', '892');
INSERT INTO `listStandard1` VALUES (27, '[YY/T 0506.6-2009] 病人、医护人员和器械用手术单、手术衣和洁净服 第6部分：阻湿态微生物穿透试验方法', '现行', 'YY/T 0506本部分规定了一项试验方法和相关的试验仪器，可用于测定材料在经受机械摩擦时阻液体中细菌穿透的性能。', '2010-12-01', '国家食品药品监督管理局', '3908', '防护服相关标准', '3899', '1', '3', '892');
INSERT INTO `listStandard1` VALUES (28, '[YY/T 0506.5-2009] 病人、医护人员和器械用手术单、手术衣和洁净服 第5部分：阻干态微生物穿透试验方法', '现行', 'YY/T0506的本部分规定了用于评定屏障材料对携菌微粒阻穿透性的试验方法。', '2010-12-01', '国家食品药品监督管理局', '3908', '防护服相关标准', '3899', '1', '3', '892');
INSERT INTO `listStandard1` VALUES (29, '[YY/T 0506.4-2016] 病人、医护人员和器械用手术单、手术衣和洁净服第4部分：干态落絮试验方法', '现行', 'YY/T 0506的本部分规定了干态条件下测量手术单、手术衣和洁净服及材料落絮的试验方法。该方法也适合于其他医用纺织材料。', '2017-06-01', '国家食品药品监督管理总局', '3908', '防护服相关标准', '3899', '1', '3', '892');
INSERT INTO `listStandard1` VALUES (30, '[YY/T 0506.2-2016] 病人、医护人员和器械用手术单、手术衣和洁净服 第2部分：性能要求和试验方法', '现行', '本标准规定了评价手术单、手术衣和洁净服的性能要求和试验方法。本标准适用于病人、医护人员和器械用手术单、手术衣和洁净服。本标准代替YY/T 0506.2-2009病人、医护人员和器械用手术单、手术衣和洁净服第2部分：性能要求和性能水平和YY/T 0506.3-2005病人、医护人员和器械用手术单、手术衣和洁净服第3部分：试验方法。', '2017-01-01', '国家食品药品监督管理总局', '3908', '防护服相关标准', '3899', '1', '3', '892');
INSERT INTO `listStandard1` VALUES (31, '[YY/T 0506.1-2005] 病人、医护人员和器械用手术单、手术衣和洁净服 第1部分:制衣厂、处理厂和产品的通用要求', '现行', 'YY/T 0506的本部分规定了向使用方和第三方认证提供的除医疗器械一般性标签（见YY 0466和EN 1041）以外的信息，涉及了制造和加工要求。本部分对病人、医护人员和器械的一次性使用和重复性使用的手术单、手术衣和洁净服的性能给出了通用指南，以防止手术过程和其他有创检查中病人和医护人员之间感染原的传播。本部分不包括外科口罩、外科手套、包装材料、鞋、帽以及手术切口膜。GB 7543给出了医用手套的要求，而ISO 11607则给出了包装材料的要求。本部分不包括激光手术中所用产品的易燃性要求。ISO 11810中给出了激光照射时的易燃性和阻穿透性试验方法，以及相应的分级体系。其他适合于手术衣、手术单的基本要求将包括在YY/T 0506.2中。', '2006-01-01', '', '3908', '防护服相关标准', '3899', '1', '3', '892');
INSERT INTO `listStandard1` VALUES (32, '[YY 0318-2000] 医用诊断X射线辐射防护器具 第3部分：防护服和性腺防护器具', '现行', '本标准适用于防护器具例如在进行放射线检查和介入诊治期间，当X射线管电压在150kV以下时用于人体防护的防护服。注:仅仅依靠防护服自身。并不能为人体提供足够的防护，但是，它可以减少当采用其他的x射线辐射防护方法不能够胜任或不适用时人体所受到的剂量。', '2000-07-01', '国家药品监督管理局', '3908', '防护服相关标准', '3899', '1', '3', '892');
INSERT INTO `listStandard1` VALUES (33, '[GB/T 29511-2013] 防护服装 固体颗粒物化学防护服', '现行', '本标准规定了固体颗粒物化学防护服的分级、基本性能要求、测试方法以及标识。本标准适用于防护作业场所环境中存在颗粒物化学污染的工作人员皮肤防护所需的防护服。本标准不包括配套使用的呼吸防护用品、防护手套、防护鞋等其他个体防护装备的技术要求。', '2014-02-01', '中华人民共和国国家质量监督检验检疫总局  中国国家标准化管理委员会', '3908', '防护服相关标准', '3899', '1', '3', '892');
INSERT INTO `listStandard1` VALUES (34, '[GB 24540-2009] 防护服装 酸碱类化学品防护服', '现行', '本标准规定了酸碱类化学品防护服的分级、技术要求、测试方法、检验规则及标识等。本标准适用于工业作业场所作业人员使用的防护液态酸碱类化学品的防护服。本标准不适用于消防、应急救援等作业场所使用的酸碱类化学品防护服。本标准不适用于针对氢氟酸、氨水、有机酸碱的防护服。', '2010-09-01', '中华人民共和国国家质量监督检验检疫总局  中国国家标准化管理委员会', '3908', '防护服相关标准', '3899', '1', '3', '892');
INSERT INTO `listStandard1` VALUES (35, '[GB 24539-2009] 防护服装 化学防护服通用技术要求', '现行', '本标准规定了化学防护服的分类、分级和标识,确立了化学防护服基本技术要求和试验方法。本标准适用于从业人员在作业场所及应急救援工作中所需要的化学防护服。本标准不适用于消防等场合使用的化学防护服。本标准不专门提出手套、防护靴/鞋、防护面具、视窗、安全眼镜以及呼吸装置等个体防护装备的性能指标要求,除非该防护装备属于防护服整体的一部分,并提供相应的化学防护性能。', '2010-09-01', '中华人民共和国国家质量监督检验检疫总局  中国国家标准化管理委员会', '3908', '防护服相关标准', '3899', '1', '3', '892');
INSERT INTO `listStandard1` VALUES (36, '[GB/T 24536-2009] 防护服装 化学防护服的选择、使用和维护', '现行', '本标准规定了化学防护服的选择、使用和维护。本标准适用于作业人员在作业环境及应急救援活动中所使用的化学防护服。', '2010-09-01', '中华人民共和国国家质量监督检验检疫总局  中国国家标准化管理委员会', '3908', '防护服相关标准', '3899', '1', '3', '892');
INSERT INTO `listStandard1` VALUES (37, '[GB/T 23462-2009] 防护服装 化学物质渗透试验方法', '现行', '本标准规定了在持续或间歇接触条件下,化学防护材料抗液态或气态化学物质渗透性能的试验方法。本标准只适用于不透气化学防护材料渗透性能的测试。', '2009-12-01', '中华人民共和国国家质量监督检验检疫总局  中国国家标准化管理委员会', '3908', '防护服相关标准', '3899', '1', '3', '892');
INSERT INTO `listStandard1` VALUES (38, '[YY/T 0616.5-2019] 一次性使用医用手套 第5部分：抗化学品渗透 持续接触试验方法', '即将实施', '本标准规定了在持续接触条件下，一次性使用医用手套材料抗液体化学品渗透的试验条件、试验程序及结果报告。本标准适用于对一次性使用医用手套进行抗化学品渗透性能评价。', '2020-10-01', '国家药品监督管理局', '3909', '医用手套相关标准', '3899', '1', '3', '893');
INSERT INTO `listStandard1` VALUES (39, '[YY/T 0616.4-2018] 一次性使用医用手套 第4部分：抗穿刺试验方法', '现行', '本标准规定了一次性使用医用手套抗穿刺性能的试验条件、试验程序及结果报告。本标准适用于对一次性使用医用手套的抗穿刺性能进行评价。', '2020-01-01', '国家药品监督管理局', '3909', '医用手套相关标准', '3899', '1', '3', '893');
INSERT INTO `listStandard1` VALUES (40, '[YY/T 0616.3-2018] 一次性使用医用手套 第3部分：用仓贮中的成品手套确定实际时间失效日期的方法', '现行', '本标准规定了用以确定在其最终包装中、贮存在典型仓贮条件下的医用手套的货架寿命的研究设计。本标准适用于所有由合成或天然胶乳制成的外科和检查手套。', '2019-11-01', '国家药品监督管理局', '3909', '医用手套相关标准', '3899', '1', '3', '893');
INSERT INTO `listStandard1` VALUES (41, '[GB/T 24787-2009] 一次性使用非灭菌橡胶外科手套', '现行', '', '2010-10-01', '中华人民共和国国家质量监督检验检疫总局、中国国家标准化管理委员会', '3909', '医用手套相关标准', '3899', '1', '3', '893');
INSERT INTO `listStandard1` VALUES (42, '[JB/T 20180-2017] 隔离装置用手套检漏仪', '现行', '本标准规定了隔离装置用手套检漏仪的术语和定义、分类与标记、要求、试验方法、检验规则和标志、使用说明书、包装、运输与贮存。本标准适用于制药行业隔离装置用操作手套的密封性测试的检漏仪。', '2017-07-01', '中华人民共和国工业和信息化部', '3909', '医用手套相关标准', '3899', '1', '3', '893');
INSERT INTO `listStandard1` VALUES (43, '[GB 7543-2006] 一次性使用灭菌橡胶外科手套', '现行', '本标准规定了用于外科操作中以保护病人和使用者、避免交叉感染的有包装的灭菌的橡胶手套的技术要求。适用于穿戴一次然后丢弃的一次性手套，不适用于检查手套或程序手套。本标准规定了橡胶外科手套的性能和安全性。但外科手套的安全、正确地使用和灭菌过程及随后处理的过程和储存过程都不适用于本标准。', '2006-12-01', '中华人民共和国国家质量监督检验检疫总局 中国国家标准化管理委员会', '3909', '医用手套相关标准', '3899', '1', '3', '893');
INSERT INTO `listStandard1` VALUES (44, '[YY/T 0616.2-2016] 一次性使用医用手套 第2部分：测定货架寿命的要求和试验', '现行', '本标准规定了一次性使用手套货架寿命的要求、标签和与试验方法有关的信息公开的要求。本标准适用于已上市的、未上市的以及设计有重大变化的产品。', '2017-01-01', '国家食品药品监督管理总局', '3909', '医用手套相关标准', '3899', '1', '3', '893');
INSERT INTO `listStandard1` VALUES (45, '[YY/T 0616.1-2016] 一次性使用医用手套 第1部分：生物学评价要求与试验', '现行', '本标准规定了一次性使用医用手套生物学安全性评价的要求，给出了标签和所用试验方法的信息的要求。本标准适用于一次性使用医用手套生物学评价。本标准代替YY/T 0616-2007一次性使用医用手套生物学评价要求与试验。', '2017-01-01', '国家食品药品监督管理总局', '3909', '医用手套相关标准', '3899', '1', '3', '893');
INSERT INTO `listStandard1` VALUES (46, '[GB/T 32103-2015] 织物浸渍胶乳防护手套', '现行', '本标准规定了织物浸渍胶乳防护手套的术语和定义、分类、要求、检验规则、试验方法、包装、标志、运输和贮存。本标准适用于以织物为内衬、表面经过浸渍一定的胶乳而制成的用于手部一般防护用的手套(以下简称“手套”)。', '2016-06-01', '中华人民共和国国家质量监督检验检疫总局  中国国家标准化管理委员会', '3909', '医用手套相关标准', '3899', '1', '3', '893');
INSERT INTO `listStandard1` VALUES (47, '[GB/T 29512-2013] 手部防护 防护手套的选择、使用和维护指南', '现行', '本标准规定了防护手套的选择、使用和维护的原则、方法与要求。本标准适用于建筑、机械制造、金属冶炼、化工、电子等行业为预防作业中物理和化学因素伤害手部而需要的防护手套,不适用于医疗、食品加工行业的微生物防护手套。', '2014-02-01', '中华人民共和国国家质量监督检验检疫总局  中国国家标准化管理委员会', '3909', '医用手套相关标准', '3899', '1', '3', '893');
INSERT INTO `listStandard1` VALUES (48, '[GB 28881-2012] 手部防护 化学品及微生物防护手套', '现行', '本标准规定了化学品及微生物防护手套的技术要求、试验方法及标识。本标准适用于职业用化学品及微生物防护手套。', '2013-05-01', '中华人民共和国国家质量监督检验检疫总局  中国国家标准化管理委员会', '3909', '医用手套相关标准', '3899', '1', '3', '893');
INSERT INTO `listStandard1` VALUES (49, '[GB/T 25915.7-2010] 洁净室及相关受控环境　第7部分：隔离装置（洁净风罩、手套箱、隔离器、微环境）', '现行', 'GB/T 25915的本部分规定了设计、建造、安装、检测和批准隔离装置的最低要求，这些要求有别于GB/T 25915.4和GB/T 25915.5对洁净室的规定。', '2011-06-01', '中华人民共和国国家质量监督检验检疫总局  中国国家标准化管理委员会', '3909', '医用手套相关标准', '3899', '1', '3', '893');
INSERT INTO `listStandard1` VALUES (50, '[GB 24788-2009] 医用手套表面残余粉末、水抽提蛋白质限量', '现行', '本标准规定了医用手套表面残余粉末、水抽提蛋白质的限量。本标准适用于一次性使用医用橡胶检查手套、一次性使用灭菌橡胶外科手套、一次性使用医用丁腈橡胶检查手套、一次性使用聚氯乙烯医用检查手套和一次性使用非灭菌橡胶外科手套。', '2010-10-01', '中华人民共和国国家质量监督检验检疫总局  中国国家标准化管理委员会', '3909', '医用手套相关标准', '3899', '1', '3', '893');
INSERT INTO `listStandard1` VALUES (51, '[GB 24786-2009] 一次性使用聚氯乙烯医用检查手套', '现行', '本标准规定了非灭菌或灭菌包装的作为医用检查和诊断过程中防止病人和使用者之间交叉感染的聚氯乙烯手套要求或治疗病患时为防止交叉感染而使用的聚氯乙烯手套要求,也包括用于处理污染性医疗材料的聚氯乙烯手套。本标准规定了一次性使用聚氯乙烯医用检查手套性能和安全性的要求,但检查手套的安全、正确使用和灭菌过程及随后的处理、包装和贮存过程不在本标准的范围之内。本标准适用于医用检查、诊断或治疗病患以及处理污染性医疗材料的一次性使用聚氯乙烯医用检查手套。', '2010-10-01', '中华人民共和国国家质量监督检验检疫总局  中国国家标准化管理委员会', '3909', '医用手套相关标准', '3899', '1', '3', '893');
INSERT INTO `listStandard1` VALUES (52, '[GB/T 21870-2008] 天然胶乳医用手套水抽提蛋白质的测定 改进Lowry法', '现行', '本标准为首次发布。本标准规定了天然胶乳医用手套水抽提蛋白质含量的测定，也适用于其他天然胶乳制品中水抽提蛋白质含量的测定，但抽提过程和次数没有得到证实，会随试验样品类型不同而变化。本标准等同采用ＩＳＯ１２２４３：２００３《天然胶乳医用手套水抽提蛋白质的测定　改进Ｌｏｗｒｙ法》（英文版）。', '2008-10-01', '中华人民共和国国家质量监督检验检疫总局  中国国家标准化管理委员会', '3909', '医用手套相关标准', '3899', '1', '3', '893');
INSERT INTO `listStandard1` VALUES (53, '[GB/T 21869-2008] 医用手套表面残余粉末的测定', '现行', '本标准为首次发布。本标准规定了医用橡胶手套表面易去除粉末含量的测定方法，方法Ａ 适用于“有粉”手套，方法B和方法C适用于“无粉”手套。本标准不涉及与手套表面粉末相关的安全性问题，也不规定手套表面残余粉末的限量。非橡胶制造的医用手套不适用于本标准。本标准等同采用ＩＳＯ２１１７１：２００６《医用手套表面残余粉末的测定》（英文版）。', '2008-10-01', '中华人民共和国国家质量监督检验检疫总局  中国国家标准化管理委员会', '3909', '医用手套相关标准', '3899', '1', '3', '893');
INSERT INTO `listStandard1` VALUES (54, '[GB 10213-2006] 一次性使用橡胶检查手套', '现行', '本标准规定了灭菌或非灭菌的、作为医用检查和诊断治疗过程中防止病人和使用者之间交叉感染的橡胶检查手套要求，也包括用于处理受污染医疗材料橡胶检查手套。手套的表面是光面或麻面的。本标准规定了橡胶检查手套性能和安全性的要求，但检查手套的安全、正确使用和灭菌过程及随后的处理和贮存过程不在本标准的范围之内。', '2006-12-01', '国家质量监督检验检疫总局和国家标准化管理委员会', '3909', '医用手套相关标准', '3899', '1', '3', '893');
INSERT INTO `listStandard1` VALUES (55, '[QB/T 5069-2017] 防护手套用聚氨酯超细纤维合成革', '现行', '本标准规定了防护手套用聚氨酯超细纤维合成革的分类、要求、试验方法、检验规则、标志、包装、运输和贮存。    本标准适用于以双组分或多组分复合纤维制成的非织造布为底基，经聚氨酯树脂浸渍、湿法凝固、减量处理及后整理等一系列工艺制成的防护手套用聚氨酯超细纤维合成革。', '2017-07-01', '中华人民共和国工业和信息化部', '3909', '医用手套相关标准', '3899', '1', '3', '893');
INSERT INTO `listStandard1` VALUES (56, '[AQ 6104-2007] 防X线手套', '现行', '本标准规定了防X线手套的技术要求和试验方法。本标准适用于橡胶手套，其他材料的具有防X线性能的手套要参照本标准有关条文执行。', '2007-04-01', '国家安全生产监督管理总局', '3909', '医用手套相关标准', '3899', '1', '3', '893');
INSERT INTO `listStandard1` VALUES (57, '[AQ 6102-2007] 耐酸(碱)手套', '现行', '本标准规定了耐酸碱手套的技术要求、试验方法和标识。本标准适用于接触酸碱溶液时戴用的防护手套。', '2007-04-01', '国家安全生产监督管理总局', '3909', '医用手套相关标准', '3899', '1', '3', '893');
INSERT INTO `listStandard1` VALUES (58, '[CCGF 304.4-2015] 消毒剂产品质量监督抽查实施规范', '现行', '', '2015-06-01', '', '3910', '消毒防护相关标准', '3899', '1', '3', '894');
INSERT INTO `listStandard1` VALUES (59, '[QB/T 5367-2019] 家用蒸汽消毒锅', '现行', '本标准规定了家用蒸汽消毒锅的术语和定义、分类与命名、要求、试验方法、检验规则﹑标志﹑包装﹑运输、贮存。本标准适用于单相器具额定电压不超过250V，主要用于婴幼儿奶瓶、餐具等消毒的家用和类似用途蒸汽消毒锅。本标准不适用于有压力容器并产生蒸煮压力的蒸汽消毒器具，不适用于医疗器械等特殊用途。', '2020-01-01', '中华人民共和国工业和信息化部', '3910', '消毒防护相关标准', '3899', '1', '3', '894');
INSERT INTO `listStandard1` VALUES (60, '[WS/T 648-2019] 空气消毒机通用卫生要求', '现行', '本标准规定了空气消毒机的分类、命名与型号、技术要求、检验方法、使用方法、铭牌和说明书。本标准适用于利用物理因子、化学因子和其他因子消毒的空气消毒机。', '2019-07-01', '国家卫生健康委员会', '3910', '消毒防护相关标准', '3899', '1', '3', '894');
INSERT INTO `listStandard1` VALUES (61, '[WS/T 646-2019] 过碳酸钠消毒剂卫生要求', '现行', '', '2019-07-01', '国家卫生健康委员会', '3910', '消毒防护相关标准', '3899', '1', '3', '894');
INSERT INTO `listStandard1` VALUES (62, '[YY/T 0734.1-2018] 清洗消毒器 第1部分：通用要求和试验', '现行', '本标准规定了自动控制的清洗消毒器及其附件的术语和定义、通用要求、试验方法、标志与使用说明书、包装、运输和贮存等。本标准适用于对可重复使用的医疗器械和对医疗机构等领域的物品进行清洁和消毒的清洗消毒器。处理特殊负载的清洗消毒器的要求和试验由YY/T 0734的其他部分或其他标准规定。', '2019-11-01', '国家药品监督管理局', '3910', '消毒防护相关标准', '3899', '1', '3', '894');
INSERT INTO `listStandard1` VALUES (63, '[YY/T 0734.3-2018] 清洗消毒器 第3部分：对人体废弃物容器进行湿热消毒的清洗消毒器 要求和试验', '现行', '本标准规定了采用单个工作周期对盛接人体废弃物容器进行清空、冲洗、清洁和湿热消毒的清洗消毒器的专用要求。本标准要求与YY/T 0734.1—2018中规定的通用要求合并使用。', '2020-01-01', '国家药品监督管理局', '3910', '消毒防护相关标准', '3899', '1', '3', '894');
INSERT INTO `listStandard1` VALUES (64, '[YY/T 0734.2-2018] 清洗消毒器 第2部分：对外科和麻醉器械等进行湿热消毒的清洗消毒器 要求和试验', '现行', '本标准规定了预期在单一工作周期对可重复使用医疗器械，例如外科器械、麻醉器械等进行清洗和湿热消毒的清洗消毒器的专用要求。本标准要求与YY/T 0734.1-2018中规定的通用要求合并使用。', '2020-01-01', '国家药品监督管理局', '3910', '消毒防护相关标准', '3899', '1', '3', '894');
INSERT INTO `listStandard1` VALUES (65, '[WS 628—2018] 消毒产品卫生安全评价技术要求', '现行', '该标准代替《消毒产品卫生安全评价规定》（国卫监督发〔2014〕36号）中相关技术内容的规定（第五条、第六条、第八条、第九条、第十条、第十二条、第十三条、附件1、附件2和附件3）。', '2019-03-01', '国家卫生健康委员会', '3910', '消毒防护相关标准', '3899', '1', '3', '894');
INSERT INTO `listStandard1` VALUES (66, '[GB/T 36758-2018] 含氯消毒剂卫生要求', '现行', '本标准规定了含氯消毒剂的原料要求、技术要求、应用范围、使用方法、运输储存包装要求、标识要求、检验方法。本标准适用于以有效氯为主要杀菌成分的消毒剂，包括次氯酸钠、次氯酸钙、液氯、氯胺、二氯异氰脲酸钠、三氯异氰脲酸、氯化磷酸三钠、二氯海因、次氯酸等，但不包括以它们为杀菌成分之一复配的消毒剂。', '2019-04-01', '国家市场监督管理总局、中国国家标准化管理委员会', '3910', '消毒防护相关标准', '3899', '1', '3', '894');
INSERT INTO `listStandard1` VALUES (67, '[GB 27947-2011] 酚类消毒剂卫生要求', '现行', '', '2012-05-01', '中华人民共和国卫生部、中国国家标准化管理委员会', '3910', '消毒防护相关标准', '3899', '1', '3', '894');
