INSERT INTO `subset` VALUES (1, 7, 'GB 2626-2019', '2019-12-31', '2020-07-01', 'None', '中国标准出版社', 'None', '中国标准出版社', '', '呼吸保护装置 ', '', '', '', '国家市场监督管理总局  国家标准化管理委员会', '', '本标准规定了自吸过滤式防颗粒物呼吸器的分类和标记、技术要求、检测方法和标识。 本标准适用于防护颗粒物的自吸过滤式呼吸器。 本标准不适用于防护有害气体和蒸气的呼吸器，不适用于缺氧环境、水下作业、逃生和消防用呼吸器。', '', '', '替代GB 2626-2006', '', '', '呼吸防护 自吸过滤式防颗粒物呼吸器 ', '即将实施', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/01/26/2yHMr55hsX.pdf', '', '', '', '', '', '');
INSERT INTO `subset` VALUES (2, 8, 'YY/T 1497-2016', '2016-07-29', '2017-06-01', 'None', '中国标准出版社', '刘思敏、金国胜、李成志、潘四春', '中国标准出版社', ' 医用卫生用品', '医院设备 ', '国家食品药品监督管理总局', '北京市医疗器械检验所、青岛众瑞智能仪器有限公司', '国家食品药品监督管理局北京医疗器械质量监督检验中心', '国家食品药品监督管理总局', '', '本标准规定了用Phi-X174噬菌体悬浮液为替代微生物，对医用防护口罩或口罩材料进行病毒过滤效率的测试方法。本标准适用于有病毒过滤效率评价要求的医用防护口罩或口罩材料。', '', '', '', '', '', '医用防护口罩材料病毒过滤效率评价测试方法 Phi-X174噬菌体测试方法 ', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/07/MZTYNiXZBp.pdf', '', '', '', '', '', '');
INSERT INTO `subset` VALUES (3, 9, 'YY/T 0969-2013', '2013-10-21', '2014-10-01', 'None', '中国标准出版社', '刘思敏、黄永富、岳卫华、贺学英、胡广勇', '中国标准出版社', ' 医用卫生用品', '医院设备 ', '国家食品药品监督管理总局', '北京市医疗器械检验所', '国家食品药品监督管理局北京医疗器械质量监督检验中心', '国家食品药品监督管理局', '', '本标准规定了一次性使用医用口罩的技术要求、试验方法、标志、使用说明书、包装、运输和贮存。本标准适用于覆盖使用者的口、鼻及下颌，用于普通医疗环境中佩戴、阻隔口腔和鼻腔呼出或喷出污染物的一次性使用口罩。本标准不适用于医用防护口罩、医用外科口罩。', '\r\n                本标准按照GB/T1.1—2009给出的规则起草。请注意,本文件的某些内容可能涉及专利。本文件的发布机构不承担识别这些专利的责任。本标准由国家食品药品监督管理总局提出。本标准由国家食品药品监督管理局北京医疗器械质量监督检验中心归口。本标准起草单位:北京市医疗器械检验所。本标准主要起草人:刘思敏、黄永富、岳卫华、贺学英、胡广勇。', '', '', '下列文件对于本文件的应用是必不可少的。凡是注日期的引用文件,仅注日期的版本适用于本文件。凡是不注日期的引用文件,其最新版本(包括所有的修改单)适用于本文件。;GB/T14233.1—2008 医用输液、输血、注射器具检验方法 第1部分:化学分析方法;GB/T14233.2—2005 医用输液、输血、注射器具检验方法 第2部分:生物学试验方法;GB15979—2002 一次性使用卫生用品卫生标准;GB/T16886.5 医疗器械生物学评价 第5部分:体外细胞毒性试验;GB/T16886.10 医疗器械生物学评价 第10部分:刺激与迟发型超敏反应试验;GB/T16886.12 医疗器械生物学评价 第12部分:样品制备与参照样品;YY0469 医用外科口罩', '', '一次性使用医用口罩 ', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/01/27/wSdyTxzBx6.pdf', '', '', '', '', '', '');
