INSERT INTO `subset` VALUES (56, 62, 'YY/T 0734.1-2018', '2018-11-07', '2019-11-01', 'None', '中国标准出版社', '陈琨、屈道银、胡昌明、阎鹏、徐伟雄、王洪敏、黄鸿新、朱玲燕、张扬', '中国标准出版社', ' 公共医疗设备', '消毒设备 ', '国家药品监督管理局', '山东新华医疗器械股份有限公司、广东省医疗器械质量监督检验所、昆山超声仪器有限公司、倍力曼医疗设备(上海)有限公司', '全国消毒技术与设备标准化技术委员会(SAC/TC 200)', '国家药品监督管理局', '', '本标准规定了自动控制的清洗消毒器及其附件的术语和定义、通用要求、试验方法、标志与使用说明书、包装、运输和贮存等。本标准适用于对可重复使用的医疗器械和对医疗机构等领域的物品进行清洁和消毒的清洗消毒器。处理特殊负载的清洗消毒器的要求和试验由YY/T 0734的其他部分或其他标准规定。', '', '', '替代YY/T 0734.1-2009', '', 'ISO 15883-1:2006+Amendment 1:2014 MOD', '清洗消毒器 第1部分：通用要求和试验 ', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/07/hsrhmKHdm4.pdf', '', '', '', '', '', '');
