INSERT INTO `subset` VALUES (58, 64, 'YY/T 0734.2-2018', '2018-12-01', '2020-01-01', 'None', '中国标准出版社', '阎鹏、胡昌明、陈琨、王洪敏、龚琬玲、王仁礼、李杨玲', '中国标准出版社', ' 公共医疗设备', '消毒设备 ', '国家药品监督管理局', '山东新华医疗器械股份有限公司、昆山超声仪器有限公司、广东省医疗器械质量监督检验所', '全国消毒技术与设备标准化技术委员会(SAC/TC 200)', '国家药品监督管理局', '', '本标准规定了预期在单一工作周期对可重复使用医疗器械，例如外科器械、麻醉器械等进行清洗和湿热消毒的清洗消毒器的专用要求。本标准要求与YY/T 0734.1-2018中规定的通用要求合并使用。', '', '', '替代YY/T 0734.2-2009', '', '', '清洗消毒器 第2部分：对外科和麻醉器械等进行湿热消毒的清洗消毒器 要求和试验 ', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/07/pmWsxYrceN.pdf', '', '', '', '', '', '');
