INSERT INTO `subset` VALUES (70, 76, 'YY/T 1478-2016', '2016-07-29', '2017-06-01', 'None', '中国标准出版社', '张群、颜蕴琦、王洪敏、胡昌明、张帆', '中国标准出版社', ' 公共医疗设备', '有关消毒和灭菌的其他标准 ', '国家食品药品监督管理总局', '山东新华医疗器械股份有限公司、成都老肯科技股份有限公司、国家食品药品监督管理局广州医疗器械质量监督检验中心', '全国消毒技术与设备标准化技术委员会(SAC/TC 200)', '国家食品药品监督管理总局', '', '本标准规定了可重复使用医疗器械在清洗、消毒和灭菌处理过程中必要的追溯信息。本标准适用于清洗、消毒和灭菌设备或质量追溯软件的设计参考。本标准不适用于对可重复使用医疗器械在处理过程中其余环节的追溯信息的规定，如回收、分类、存储等内容。', '', '', '', '', '', '可重复使用医疗器械消毒灭菌的追溯信息 ', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/08/tFnQf2aQfT.pdf', '', '', '', '', '', '');
