INSERT INTO `subset` VALUES (79, 85, 'JJG 111-2003', '2003-05-12', '2003-11-12', 'None', '中国计量出版社', '张克、李谦等', '中国计量出版社', ' 热学计量', '', '', '北京市计量科学研究所、上海华辰医用仪表有限公司', '全国温度计量技术委员会', '中华人民共和国国家质量监督检验检疫总局', '', '本规程适用于测量范围在30.0℃ ~43.0℃ ,具有最高留点结构、感温液体为水银或其他金属液体的玻璃体温计的首次检定。', '', '', 'JJG 111-1989', '', '', '玻璃体温计检定规程 ', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/08/nZEBQrS5cA.pdf', '', '', '', '', '', '');
INSERT INTO `subset` VALUES (80, 86, 'JJF 1412-2013', '2013-06-27', '2013-09-27', 'None', '中国质检出版社 中国标准出版社', '张克、张易农等', '中国质检出版社 中国标准出版社', '', '', '', '北京市计量检测科学研究院、中国计量科学研究院等', '全国温度计量技术委员会', '国家质量监督检验检疫总局', '', '本规范适用于测量范围为35.5℃~42.0℃用于测量人体温度的临床用变色体温计的校准。', '\r\n                引 言本规范依据JJF1071—2010 《国家计量校准规范编写规则》编制,其中测量结果不确定度的评定依据JJF1059.1—2012 《测量不确定度评定与表示》进行。本规范计量特性中的测温范围、分度值、留存时间和示值误差分别引用EN12470-2:2000+A1:2009 《临床用体温计 第2部分 相变型(点阵)体温计》[Clinicalthermometers—Part2:Phasechangetype(dotmatrix)thermometers]中的5、6.1、6.2和6.5。本规范校准条件中的环境温度引用ASTME825-98 (Reapproved2009)《一次性使用的断续测量人体温度的相变型体温计》(PhaseChange—TypeDisposableFeverThermometerforIntermittentDeterminationofHumanTemperature)中的5.5和ASTME1299-96 (Reapproved2010) 《可重复使用的断续测量人体温度的相变型体温计》(ReusablePhase—Change—TypeFeverThermometerforIntermittentDeterminationofHumanTemperature)中的5.5。EN12470-2:2000+ A1:2009 中规定的测量范围包括ASTM E 825-98(Reapproved2009)和ASTM E1299-96 (Reapproved2010)规定的测量范围,更符合实际情况。本规范为首次制定。', '目 录;引言 (Ⅱ);1 范围 (1);2 引用文件 (1);3 术语 (1);3.1 变色体温计 (1);3.2 可重复使用体温计 (1);3.3 一次性体温计 (1);3.4 温度补偿值 (1);3.5 预调整体温计 (1);3.6 感温点 (1);3.7 感温区域 (2);3.8 测量时间 (2);3.9 留存时间 (2);3.10 失效日期 (2);4 概述 (2);5 计量特性 (2);5.1 测温范围 (2);5.2 分度值 (2);5.3 留存时间 (2);5.4 示值误差 (2);6 校准条件 (3);6.1 环境条件 (3);6.2 测量用标准器及配套设备 (3);7 校准项目和校准方法 (3);7.1 校准项目 (3);7.2 校准方法 (3);7.3 校准结果的处理 (4);8 校准结果的表达 (4);9 复校时间间隔 (5);附录A 临床用变色体温计校准原始记录 (6);附录B 临床用变色体温计校准证书(内页)格式 (7);附录C 体温计放入恒温槽中校准的操作方法 (8);附录D 临床用变色体温计测量结果不确定度评定 (9)', '', '本规范引用下列文件:;EN12470-2:2000+A1:2009 临床用体温计 第2部分 相变型(点阵)体温计[Clinicalthermometers—Part2:Phasechangetype(dotmatrix)thermometers];ASTM E825-98 (Reapproved2009) 一次性使用的断续测量人体温度的相变型体;温计(PhaseChange—TypeDisposableFeverThermometerforIntermittentDeterminationofHumanTemperature);ASTM E1299-96(Reapproved2010) 可重复使用的断续测量人体温度的相变型体温计(ReusablePhase—Change—TypeFeverThermometerforIntermittentDeterminationofHumanTemperature);凡是注日期的引用文件,仅注日期的版本适用于本规范;凡是不注日期的引用文件,其最新版本(包括所有的修改单)适用于本规范。', '', '临床用变色体温计校准规范 ', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/08/5SaaBm8eYr.pdf', '', '', '', '', '', '');
INSERT INTO `subset` VALUES (81, 87, 'JJF 1226-2009', '2009-07-10', '2009-10-10', 'None', '中国质检出版社', '沈才忠、周连琴等', '中国质检出版社', ' 热学计量', '热力学和温度测量 ', '', '浙江省计量科学研究院、中国计量学院、上海市计量测试技术研究院等', '全国温度计量技术委员会', '国家质量监督检验检疫总局', '', '本规范适用于由内部电池供电，间歇测量人体或动物腋下、口腔、直肠等不同部位体温，以数字形式显示所测温度值并具有最高温度保持功能的接触式非预测型医用电子体温计（以下简称体温计）的校准。', '', '1 范围 (1);2 引用文献 (1);3 术语 (1);4 概述 (1);5 计量特性 (2);5.1 测量范围 (2);5.2 分辨力 (2);5.3 示值允许误差限 (2);5.4 功能性检查 (2);6 校准条件 (3);6.1 环境条件 (3);6.2 测量标准及其他设备 (3);7 校准项目和校准方法 (3);7.1 校准项目 (3);7.2 校准前的检查 (3);7.3 校准方法 (4);7.4 数据处理 (4);8 校准结果的表达 (5);9 复校时间间隔 (6);附录A 校准记录格式示例 (7);附录B 校准证书内页参考格式 (9);附录C 标准铂电阻温度计在(30~45)℃时的参考函数表 (10);附录D 测量不确定度分析实例 (11)', '', '本规范引用下列文献:;JJG111—2003 《玻璃体温计检定规程》;JJF1007—2007 《温度计量名词术语及定义》;JJF1071—2000 《国家计量校准规范编写规则》;JJF1171—2007 《温度巡回检测仪校准规范》;GB/T21416—2008 《医用电子体温计》;OIMLR115Clinicalelectricalthermometerswithmaximumdevice (带最大值显示装置的医用电子体温计);EN12470-3:2000Clinicalthermometers-Part3:Performanceofcompactelectrical thermometers (non-predictiveandpredictive)with maximum;device (医用体温计 第3部分:带最大值显示装置的紧凑型电子体温计(非预测型和预测型)的性能);ITS—90 1990年国际温标;使用本规范时,应注意使用上述引用文献的现行有效版本。', '', '医用电子体温计校准规范 ', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/08/Z2S2fr6Haj.pdf', '', '', '', '', '', '');
INSERT INTO `subset` VALUES (82, 88, ' ASTM F 3387 : 2019', '', '', '', 'American Society for Testing and Materials', '', '', '', '', '', '', '', '', '', '1.1  本规程规定了职业防护口罩的最低可接受使用规范；提供了正确选择、使用和维护防护口罩的信息和指导；包含了建立、实施和评估防护口罩计划的要求。\r\n1.2  本规程包括使用呼吸器，防止工作场所吸入有害空气污染物和缺氧空气。本惯例不包括以下内容：\r\n1.2.1 水下呼吸装置；\r\n1.2.2 飞机氧气系统；\r\n1.2.3 供应的航空服；\r\n1.2.4 在军事作战条件下使用呼吸器；\r\n1.2.5 医用吸入器和复苏器。\r\n1.3  单位-以英寸-磅单位表示的数值应视为标准值。括号中给出的值是对国际单位制的数学转换，仅供参考，不被视为标准值。\r\n1.4  本标准并非旨在解决与其使用相关的所有安全问题（如有）。本标准的使用者有责任在使用前建立适当的安全、健康和环境实践，并确定法规限制的适用性。\r\n1.5  本国际标准是根据世界贸易组织技术性贸易壁垒委员会发布的《关于制定国际标准、指南和建议的原则的决定》中确立的国际公认标准化原则制定的。', '', '', '', '', '', 'ASTM F 3387 : 2019', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/WJnyG5dhkk.pdf', '呼吸防护标准规范 Standard Practice for Respiratory Protection', '0', '', '', '', '');
INSERT INTO `subset` VALUES (83, 89, ' DHHS (NIOSH) Publication No.96-101', '', '', '', 'The National Institute for Occupational Safety and Health (NIOSH)', '', '', '', '', '', '', '', '', '', '1995年6月，联邦空气净化颗粒物呼吸器认证法规《42 CFR第84部分》发布，1995年7月10日，该法规正式生效。根据这项新法规，美国国家职业安全与健康研究所（NIOSH）认证的呼吸保护器器将在要求更严格的条件下进行测试，并为工人提供更多的保护。这些新的呼吸保护机也带来了显著的成本节约：据估计，由于新一代实用高效的呼吸机，仅医疗保健行业就将节省数百万美元。本指南旨在解释新法规，并为选择和使用经NIOSH认证的新型呼吸器提供有价值的信息。', '', '', '', '', '', 'DHHS (NIOSH) Publication No.96-101', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/08/8EKNbcJHey.pdf', '美国国家职业安全卫生研究所《微粒呼吸器的选择和使用指南》', '0', '', '', '', '');
INSERT INTO `subset` VALUES (84, 90, ' ASTM F2704 : 2017-07', '', '', '', 'American Society for Testing and Materials', '', '', 'Redline', '', '', '', '', '', '', '本标准描述了通过空气管路或电动空气净化呼吸器（PAPR）向佩戴者输送空气的防护装置的设计、性能、分类、文件、标签和认证要求。', '', '', '', '', '', 'ASTM F2704 : 2017-07', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/QASEwpZwpA.pdf', '空气馈送保护规范 SPECIFICATION FOR AIR-FED PROTECTIVE ENSEMBLES', '0', '', '', '', '');
INSERT INTO `subset` VALUES (85, 91, ' ASTM F2100:2019-08', '', '', '', 'American Society for Testing and Materials', '', '', 'Redline', '', '', '', '', '', '', '本规范涵盖用于提供医疗服务（如手术和患者护理）的医疗面罩结构中使用的材料和要求。', '', '', '', '', '', 'ASTM F2100:2019-08', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/B4pkwMKyPH.pdf', '医用口罩中使用的材料性能的标准规范 Standard Specification for Performance of Materials Used in Medical Face Masks', '0', 'ASTM F 2100 : 2011-04 ', '', '', '');
INSERT INTO `subset` VALUES (86, 92, ' ANSI/ASSE Z88.2-2015', '2015/4/1 0:00:00', '', '', 'The American Society of Safety Engineers', '', '', 'Standard', '', '', '', '', '', '', '本标准定义了呼吸器的使用，以保护人员免受吸入有害空气污染物和工作场所缺氧环境的伤害。寰标提醒：美国卫生工程学会，原ASSE(American Society of Sanitary Engineering)，已将其名称更改为ASSP。', '', '', '', '', '', 'ANSI/ASSE Z88.2-2015', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/10/QKXRbZYPST.pdf', '呼吸防护措施 Practices For Respiratory Protection', '42', 'ANSI Z88.2-1992', '', '', '');
INSERT INTO `subset` VALUES (87, 93, ' JIS T 8157：2018', '', '', '', 'Japanese Standards Association', '', '', '', '', '', '', '', '', '', '本标准规定了在工作场所和环境中其他位置使用的颗粒物的动力空气净化呼吸器，这些颗粒物可能因吸入漂浮在空气中的颗粒物而对人体造成有害影响。', '', '', '', '', '', 'JIS T 8157：2018', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/zZwei5kDpx.pdf', '用于颗粒物的电动空气净化呼吸器 Powered air purifying respirator for particulate matter', '0', '', '', '', '');
INSERT INTO `subset` VALUES (88, 94, ' JIS T 8151:2018', '', '', '', 'Japanese Standards Association', '', '', 'Revision', '', '', '', '', '', '', '本标准规定了在工作场所和环境中其他位置使用的颗粒呼吸器，这些微粒呼吸器吸入漂浮在空气中的微粒物质可能对人体造成有害影响。', '', '', '', '', '', 'JIS T 8151:2018', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/DBxjGGntfe.pdf', '颗粒呼吸器 Particulate respirators', '0', 'JIS T 8151:2005 ', '', '', '');
INSERT INTO `subset` VALUES (89, 95, ' JIS T 8159:2006', '2006/2/20 0:00:00', '', '', 'Japanese Standards Association', '', '', 'Standard', '', '', '', '', '', '', '本日本工业标准规定了呼吸防护装置的泄漏率[总向内泄漏（TL）和呼吸道入口盖（PL））的内漏]测试方法。', '', '', '', '', '', 'JIS T 8159:2006', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/cRpbTa4Wm6.pdf', '呼吸防护装置的泄漏率测试方法 Leakage rate testing method for respiratory protective devices', '11', '', '', '', '');
INSERT INTO `subset` VALUES (90, 96, ' JIS T 8150:2006', '2006/4/25 0:00:00', '', '', 'Japanese Standards Association', '', '', 'Standard', '', '', '', '', '', '', '本标准包括在工厂、矿山和其他工作场所、火灾现场、船舶、隧道、道路及其他地方（以下简称工作场所等），吸入缺氧空气、颗粒物、气体、蒸汽等，在可能对人体有害时，使用的呼吸防护设备的选择、使用和维护方法。', '', '', '', '', '', 'JIS T 8150:2006', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/WJcrKdsS23.pdf', '呼吸防护装置的选择，使用和维护指南 Guidance For Selection, Use And Maintenance Of Respiratory Protective Devices', '26', '', '', '', '');
INSERT INTO `subset` VALUES (91, 97, ' JIS T 8001:2006', '2006/3/25 0:00:00', '', '', 'Japanese Standards Association', '', '', 'Standard', '', '', '', '', '', '', '本标准涉及工厂、矿山等使用的呼吸保护具的主要用语及其定义，以及作为参考显示对应的英语及同义词。', '', '', '', '', '', 'JIS T 8001:2006', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/PQeSCXctTp.pdf', '气体呼吸器呼吸防护装置术语表 Glossary Of Terms For Respiratory Protective Devices', '22', '', '', '', '');
INSERT INTO `subset` VALUES (92, 98, ' JIS T 8152:2012', '2012/2/25 0:00:00', '', '', 'Japanese Standards Association', '', '', 'Standard', '', '', '', '', '', '', '本日本工业标准规定了，在工作区或其他地方，通过去除有毒气体或与有毒气体混合的颗粒物或空气，清除吸入空气的气体呼吸器。', '', '', '', '', '', 'JIS T 8152:2012', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/YzA88D3NWf.pdf', '气体呼吸器 Gas respirators', '26', 'JIS T 8152:2002 ', '', '', '');
INSERT INTO `subset` VALUES (93, 99, ' AS B99-1951', '', '', '', 'Standards Australia', '', '', '', '', '', '', '', '', '', '由于此存档文档的期限，历史产品副本不可用，可用信息有限，因此此记录不完整。如欲了解更多详情，请致电SAI Global国际标准驻华办公室（寰标网）客服电话。', '', '', '', '', '', 'AS B99-1951', '已作废', '#', '澳大利亚头盔、手罩、面罩和护目镜的标准规范，用于在焊接和联合作业中防止有害辐射 Australian standard specification for helmets, handshields, face masks and goggles for protection against harmful radiations in welding and allied operations', '0', '', 'AS-NZS 1337-1992', '', '');
