INSERT INTO `subset` VALUES (95, 101, ' AS/NZS 1337:1992', '1992/11/16 0:00:00', '', '', 'Standards Australia', '', '', 'Standard', '', '', '', '', '', '', '本标准指定对护目镜及相关镜片的最低要求，旨在保护工业环境中的眼部。提供的保护类型包括熔融金属、空气中的颗粒和碎片、有害气体、蒸汽和气溶胶，以及自然环境中的太阳光和光学辐射。镜片的标记也表示提供保护的类型。', '', '', '', '', '', 'AS/NZS 1337:1992', '被替代但部分可行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/11/x3GTTFBHK6.pdf', '工业用护目镜 Eye protectors for industrial applications', '40', 'DR 91189 ;AS 1337-1984 ;NZS 5816:1986 ;DR 91189 ;AS 1337-1984 ;AS B99-1951', 'AS/NZS 1337.0(Int):2010 ;AS/NZS 1337.1:2010 ;AS/NZS 1337.1:2010 ;AS/NZS 1337.0(Int):2010 ', 'AS/NZS 1337:1992/Amdt 1:1994 ;AS/NZS 1337:1992/Amdt 2:1997 ', '');
INSERT INTO `subset` VALUES (96, 102, ' AS/NZS 1801:1997', '1997/9/5 0:00:00', '', '', 'Standards Australia', '', '', 'Standard', '', '', '', '', '', '', '本标准列出了反映其预期用途的三种防护头盔的要求。它涉及头盔外壳的结构和头盔表面处理，包括测试方法。对高温作业场所使用的职业防护头盔和基于工业头盔的山火消防员头盔提出了具体的附加性能要求。', '', '', '', '', '', 'AS/NZS 1801:1997', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/11/MPK8kWmsCB.pdf', '职业防护头盔 Occupational protective helmets', '30', 'DR 94212 ;AS 1801-1981 ;AS 1801-1981 ;DR 94212 ;NZS 5806:1980 ', '', 'AS/NZS 1801:1997/Amdt 1:1999 ', '');
