INSERT INTO `subset` VALUES (106, 112, ' AS/NZS 2512.2:2006', '2006/6/13 0:00:00', '', '', 'Standards Australia', '', '', 'Standard', '', '', '', '', '', '', '本标准规定了要测试的防护头盔的调理程序、头盔准备测试的方式以及测试的实验室条件。', '', '', '', '', '', 'AS/NZS 2512.2:2006', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/11/KCS7ihb2bi.pdf', '防护头盔的检测方法 - 测试样品和实验室条件的一般要求 Methods of testing protective helmets General requirements for the conditioning and preparation of test specimens and laboratory conditions', '3', 'AS/NZS 2512.2:1998 ;DR 06104 CP ', '', '', '');
INSERT INTO `subset` VALUES (107, 113, ' AS/NZS 1337.1:2010', '2010/6/28 0:00:00', '', '', 'Standards Australia', '', '', 'Standard', '', '', '', '', '', '', '本标准规定了非处方性质的眼睛和面部保护器及相关眼睛的最低要求。它们旨在保护人的眼睛和面部免受常见职业危害，如飞扬的颗粒和碎片、灰尘、飞溅的材料和熔融金属、有害气体、蒸汽和气溶胶。本标准给出了光学质量和低、中、高和非常高抗冲击性的要求，并包括描述适当试验方法的附录。AS/NZS 1337.6给出了抗低和中冲击的处方护目镜的要求。AS/NZS 1337第4部分和第5部分中给出了激光辐射防护眼睛的要求。本标准的目的是帮助在职业环境中提供安全、高效和舒适的视觉，包括考虑在自然环境中防护太阳镜和光辐射的需要。本标准不适用于AS/NZS1338中所述工业过程中产生的光辐射防护用滤光片。', '', '', '', '', '', 'AS/NZS 1337.1:2010', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/11/2H4SYMSZHp.pdf', '个人眼睛保护 - 职业用眼睛和面部保护器 Personal eye protection Eye and face protectors for occupational applications', '98', 'DR 08199 ;AS/NZS 1337:1992 ;DR 08199 ;AS/NZS 1337:1992 ', '', 'AS/NZS 1337.1:2010/Amdt 1:2012 ;AS/NZS 1337.1:2010/Amdt 2:2018 ', '');
INSERT INTO `subset` VALUES (108, 114, ' AS ISO 16972:2015', '2015/6/30 0:00:00', '', '', 'Standards Australia', '', '', 'Standard', '', '', '', '', '', '', '本标准提供定义并指定测量单位和图形符号，以在整个与呼吸保护装置相关的一系列标准中使用。', '', '', '', '', '', 'AS ISO 16972:2015', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/11/EQA4SBBwPy.pdf', '呼吸保护装置 - 术语，定义，图形符号和测量单位 Respiratory protective devices - Terms, definitions, graphical symbols and units of measurement', '37', 'DR AS/NZS ISO 16972:2015 ;AS/NZS 1716:2012 ', '', '', 'ISO 16972:2010');
INSERT INTO `subset` VALUES (109, 115, ' AS 4381:2015', '2015/12/23 0:00:00', '', '', 'Standards Australia', '', '', 'Standard', '', '', '', '', '', '', '本标准规定了卫生保健中使用的一次性口罩的要求，在这种情况下，卫生保健工作者和病人之间的交叉污染必须保持在最低限度。', '', '', '', '', '', 'AS 4381:2015', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/11/AZjprnDjhM.pdf', '用于卫生保健的一次性口罩 Single-use face masks for use in health care', '4', 'DR AS 4381:2015 ;AS 4381-2002 ', '', '', '');
INSERT INTO `subset` VALUES (110, 116, ' ANSI/AAMI PB70:2012', '', '', '', 'American National Standards Institute', '', '', '', '', '', '', '', '', '', '本修订版旨在为用于卫生保健设施的防护服、手术防护布及配件建立最低阻隔性能要求、分类系统和相关标签要求。', '', '', '', '', '', 'ANSI/AAMI PB70:2012', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/11/aZXhz8MPEB.pdf', '医疗保健设施中使用的防护服和防护布的防液性能和分类 Liquid Barrier Performance And Classification Of Protective Apparel And Drapes Intended For Use In Health Care Facilities', '0', 'ANSI/AAMI PB70:2003 (R2009)', '', '', '');
INSERT INTO `subset` VALUES (111, 117, ' ANSI/AAMI PB70:2003 (R2009)', '', '', '', 'American National Standards Institute', '', '', '', '', '', '', '', '', '', '本修订版旨在为用于卫生保健设施的防护服、手术防护布及配件建立最低阻隔性能要求、分类系统和相关标签要求。', '', '', '', '', '', 'ANSI/AAMI PB70:2003 (R2009)', '修订', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/JdkY4fEAyd.pdf', '医疗保健设施中使用的防护服和防护布的防液性能和分类 Liquid Barrier Performance And Classification Of Protective Apparel And Drapes Intended For Use In Health Care Facilities', '0', 'ANSI/AAMI PB70:2003', 'ANSI/AAMI PB70:2012', '', '');
INSERT INTO `subset` VALUES (112, 118, ' ANSI/AAMI PB70:2003', '', '', '', 'American National Standards Institute', '', '', '', '', '', '', '', '', '', '本标准根据用于医疗保健设施的防护服和防护帘布的防液性能，建立了其分类制度，并规定了相关 的标识要求以及确定符合性的标准化测试方法。通过指定一个统一的基础来测试和标识保护服和防护帘 布，并根据这一新的分类系统提供对抵抗特性的共同理解（例如，抗液体或液载微生物渗透的功效），该 标准旨在最终帮助终端用户确定最适合特定任务或情况的防护产品的类型。', '', '', '', '', '', 'ANSI/AAMI PB70:2003', '修订', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/ABjGdQPKxH.pdf', '医疗保健设施中使用的防护服和防护布的防液性能和分类 Liquid Barrier Performance And Classification Of Protective Apparel And Drapes Intended For Use In Health Care Facilities', '0', '', 'ANSI/AAMI PB70:2003 (R2009)', '', '');
INSERT INTO `subset` VALUES (113, 119, ' ASTM F 1670/F1670M : 2017-07', '', '', '', 'American Society for Testing and Materials', '', '', 'Redline', '', '', '', '', '', '', '本标准用于评估防护服所用材料在连续液体接触条件下合成血液渗透的抗性。', '', '', '', '', '', 'ASTM F 1670/F1670M : 2017-07', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/2jZzXDyxKX.pdf', '用于合成纤维的防护服渗透材料抗性的测试方法 TEST METHOD FOR RESISTANCE OF MATERIALS USED IN PROTECTIVE CLOTHING TO PENETRATION BY SYNTHETIC BLOOD', '0', 'ASTM F 1670 : 2008-09 ', '', '', '');
INSERT INTO `subset` VALUES (114, 120, ' ASTM F 1359/F1359M : 2016-03', '', '', '', 'American Society for Testing and Materials', '', '', 'Redline', '', '', '', '', '', '', '本标准用于指定防护服或防护服的能力，以淋浴喷雾的形式，用表面活性剂处理的水抵抗液体渗透。', '', '', '', '', '', 'ASTM F 1359/F1359M : 2016-03', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/f6G2M83rcX.pdf', '人体模型喷洒下防护布或防护胶液抗液体渗透性的测试方法 TEST METHOD FOR LIQUID PENETRATION RESISTANCE OF PROTECTIVE CLOTHING OR PROTECTIVE ENSEMBLES UNDER A SHOWER SPRAY WHILE ON A MANIKIN', '0', 'ASTM F 1359 : 2007-11 ', '', '', '');
INSERT INTO `subset` VALUES (115, 121, ' ASTM F1154 : 2018', '', '', '', 'American Society for Testing and Materials', '', '', 'Redline', '', '', '', '', '', '', '本标准定义化学防护装置和装置组件，以根据其舒适性、适合性、功能和耐久性确定装置或装置组件在工作环境中的适用性。', '', '', '', '', '', 'ASTM F1154 : 2018', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/FbcWR56WsZ.pdf', '用于评估保护组合、组合元件和其他组件的舒适性、拟合性、功能性和耐用性的标准实践 Standard Practices for Evaluating the Comfort, Fit, Function, and Durability of Protective Ensembles, Ensemble Elements, and Other Components', '0', 'ASTM F1154 : 2011', '', '', '');
INSERT INTO `subset` VALUES (116, 122, ' ASTM F 739 : 2012 : EDT 1', '', '', '', 'American Society for Testing and Materials', '', '', 'Test Method', '', '', '', '', '', '', '本试验方法测量液体和气体在连续接触条件下通过防护服材料的渗透性。', '', '', '', '', '', 'ASTM F 739 : 2012 : EDT 1', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/10/dBCkz3f3sm.pdf', '连续接触条件下液体和气体通过防护服材料渗透的标准试验方法 Standard Test Method for  Permeation of Liquids and Gases through Protective Clothing  Materials under Conditions of Continuous Contact', '0', 'ASTM F 739 : 2012 ', '', '', '');
INSERT INTO `subset` VALUES (117, 123, ' ASTM F 1383 : 2012 : EDT 1', '', '', '', 'American Society for Testing and Materials', '', '', 'Test Method', '', '', '', '', '', '', '本试验方法测量液体和气体在间歇接触条件下通过防护服材料的渗透性。', '', '', '', '', '', 'ASTM F 1383 : 2012 : EDT 1', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/4rBJKDP8sz.pdf', '间歇接触条件下通过防护服材料渗透液体和气体的标准测试方法 Standard Test Method for  Permeation of Liquids and Gases through Protective Clothing  Materials under Conditions of Intermittent Contact', '0', 'ASTM F 1383 : 2012 ', '', '', '');
INSERT INTO `subset` VALUES (118, 124, ' ASTM F 1342/F1342M : 2005 : R2013 : EDT 1', '', '', '', 'American Society for Testing and Materials', '', '', 'Test Method', '', '', '', '', '', '', '本标准通过测量使尖刺探针穿透试样所需的力，描述防护服材料试样具有的穿刺阻力。', '', '', '', '', '', 'ASTM F 1342/F1342M : 2005 : R2013 : EDT 1', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/QcSGCCTnkx.pdf', '防护服材料的抗穿刺性的标准测试方法 Standard Test Method for Protective Clothing Material Resistance to Puncture', '0', 'ASTM F 1342 : 2005 ', '', '', '');
INSERT INTO `subset` VALUES (119, 125, ' ASTM F 1671/F1671M : 2013', '', '', '', 'American Society for Testing and Materials', '', '', 'Test Method', '', '', '', '', '', '', '本试验方法用于在连续液体接触条件下，使用替代微生物测量防护服所用材料对血液传播病原体的渗透阻力。', '', '', '', '', '', 'ASTM F 1671/F1671M : 2013', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/B5DwMpFKfe.pdf', '以Phi-X174噬菌体渗透作为测试系统，对防护服中所用材料的抗渗透性标准测试方法 Standard Test Method for Resistance of Materials Used in Protective Clothing to Penetration by Blood-Borne Pathogens Using Phi-X174 Bacteriophage Penetration as a Test System', '0', '', '', '', '');
INSERT INTO `subset` VALUES (120, 126, ' ASTM F 1194 : 2018', '', '', '', 'American Society for Testing and Materials', '', '', 'Guide', '', '', '', '', '', '', '本指南提供了一种记录渗透试验信息和性能数据的格式，即文件化信息和数据分为五大类，定义了每个测试的重要方面：防护服材料、试验方法、挑战化学品、测试结果及数据来源。遵循标准试验方法中概述的程序有助于本指南的使用。此外，本国际标准是根据世界贸易组织技术性贸易壁垒委员会发布的《关于制定国际标准、指南和建议的原则的决定》中确立的国际公认标准化原则制定的。', '', '', '', '', '', 'ASTM F 1194 : 2018', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/T78AhbQkpx.pdf', '防护服所用材料化学渗透测试结果记录的标准指南 Standard Guide for  Documenting the Results of Chemical Permeation Testing of Materials  Used in Protective Clothing', '0', 'ASTM F 1194 : 1999 : R2010 ;ASTM F 739 : 2012 ', '', '', '');
INSERT INTO `subset` VALUES (121, 127, ' ASTM F 903 : 2018', '2018/7/1 0:00:00', '', '', 'American Society for Testing and Materials', '', '', 'Test Method', '', '', '', '', '', '', '此测试方法用于测试防护服材料、接缝和封口等组件的试样，或用于建造防护服的接口。测试液的可见穿透电阻通过液体与试样正常外部（外部）表面持续接触来确定。', '', '', '', '', '', 'ASTM F 903 : 2018', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/7kS5G8AQDB.pdf', '防护服中所用材料的抗液体渗透性的标准测试方法 Standard Test Method for  Resistance of Materials Used in Protective Clothing to Penetration  by Liquids', '12', 'ASTM F 903 : 2017 ', '', '', '');
INSERT INTO `subset` VALUES (122, 128, ' ASTM F 2878 : 2019', '', '', '', 'American Society for Testing and Materials', '', '', 'Test Method', '', '', '', '', '', '', '此测试方法评估防护服材料的抗穿刺性，这些材料可能包括：塑料或弹性膜、涂层织物、柔性材料、层压板、皮革或纺织材料。', '', '', '', '', '', 'ASTM F 2878 : 2019', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/ZGpnsmTd66.pdf', '防护服材料对皮下注射针头的抗性的标准测试方法 Standard Test Method for  Protective Clothing Material Resistance to Hypodermic Needle  Puncture', '0', 'ASTM F 2878 : 2010 ', '', '', '');
INSERT INTO `subset` VALUES (123, 129, ' ASTM STP 1237 : 1996', '', '', '', 'American Society for Testing and Materials', '', '', 'Standard', '', '', '', '', '', '', '本标准做了以下定义：1）防护服及其部件的物理试验方法；2）化学和生物试验方法的发展和应用；3）与农药有关的防护服问题；4）防护服试验和评价；5）防护服项目管理；6）防护服标准和法规；7）热试验方法的发展和应用。', '', '', '', '', '', 'ASTM STP 1237 : 1996', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/10/zEksMtrMnS.pdf', '保护性衣服的性能：第5卷 PERFORMANCE OF PROTECTIVE CLOTHING: 5TH VOLUME', '640', '', '', '', '');
