INSERT INTO `subset` VALUES (131, 137, ' JIS T 8124-1:2010', '2010/6/1 0:00:00', '', '', 'Japanese Standards Association', '', '', 'Standard', '', '', '', '', '', '', '本日本工业标准规定了化学防护服的性能要求，为全身提供防空气固体颗粒的保护（JIS T 8115 中指定的 5 型服装）。', '', '', '', '', '', 'JIS T 8124-1:2010', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/KjGEDTkTmk.pdf', '防固体颗粒的防护服-第1部分：可保护全身免受空气中的固体颗粒的化学防护服的性能要求（第5类防护服）Protective clothing for use against solid particulates Part 1: Performance requirements for chemical protective clothing providing protection to the full body against airborne solid particulates (type 5 clothing)', '8', 'JIS T 8124-1:2008 ', '', '', '');
INSERT INTO `subset` VALUES (132, 138, ' JIS T 8030:2015', '2015/11/20 0:00:00', '', '', 'Japanese Standards Association', '', '', 'Standard', '', '', '', '', '', '', '该标准描述一种实验室测试方法，该方法能够确定防护服中使用的材料在连续或间歇性接触条件下使用的液体或气体化学品对渗透的耐受性。', '', '', '', '', '', 'JIS T 8030:2015', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/h56hKMeMrr.pdf', '防护服-化学品防护-防护服材料耐液体和气体渗透的测定 Protective Clothing - Protection Against Chemicals - Determination Of Resistance Of Protective Clothing Materials To Permeation By Liquids And Gases', '39', 'JIS T 8030:2005 ', '', '', '');
INSERT INTO `subset` VALUES (133, 139, ' JIS T 8005:2015', '2015/11/20 0:00:00', '', '', 'Japanese Standards Association', '', '', 'Standard', '', '', '', '', '', '', '本标准规定了防护服的人体工程学、老化、尺寸和标记以及制造商提供的信息的一般要求和建议。', '', '', '', '', '', 'JIS T 8005:2015', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/NmyHYwTRdx.pdf', '防护服-一般要求 Protective Clothing - General Requirements', '17', 'JIS T 8005:2005 ;JIS T 8005:2005 ', '', '', '');
INSERT INTO `subset` VALUES (134, 140, ' JIS T 8122:2015', '2015/11/1 0:00:00', '', '', 'Japanese Standards Association', '', '', 'Standard', '', '', '', '', '', '', '本标准介绍了防护服的种类和试验方法，以防止工人接触或接触生物有害物质。', '', '', '', '', '', 'JIS T 8122:2015', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/04/WYrP78ZGb2.pdf', '针对生物学上危险物质的防护服 Protective Clothing For Protection Against Hazardous Biological Agents', '16', 'JIS T 8122:2007 ', '', '', '');
INSERT INTO `subset` VALUES (135, 141, ' JIS T 8061:2015', '2015/11/1 0:00:00', '', '', 'Japanese Standards Association', '', '', 'Standard', '', '', '', '', '', '', '本标准规定了一种实验室试验方法，用于测量防护服所用材料对血液传播病原体的渗透性。', '', '', '', '', '', 'JIS T 8061:2015', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/mNmy5rkEza.pdf', '防止血液和体液接触的衣服-血液传播的病原体对防护服材料渗透的抵抗力的测定-使用Phi-x174噬菌体的测试方法 Clothing For Protection Against Contact With Blood And Body Fluids - Determination Of Resistance Of Protective Clothing Materials To Penetration By Blood-borne Pathogens - Test Method Using Phi-x174 Bacteriophage', '28', 'JIS T 8061:2010 ', '', '', '');
INSERT INTO `subset` VALUES (136, 142, ' JIS T 8032-5:2015', '2015/11/1 0:00:00', '', '', 'Japanese Standards Association', '', '', 'Standard', '', '', '', '', '', '', '本标准主要修改采用了ISO 17491-5《防护服.防化学品防护服的试验方法-第5部分：用液体喷雾法测定抗渗透性（人体模型喷雾试验）》。此外，在此标准包含虚线下划线的内容部分是更改对应ISO国际标准的内容部分。 内容更改列表及其说明可在附件 JA 找到。 本标准与 JIS T 8032 的第 4 部分不同，适用于液体化学品喷雾的化学防护服（以下简称防护服）， 定义如何确定渗透性。 本标准使用静态受试者（人体模型）代替第四部分的试验对象，通过不同的喷涂方法和测试时间进行测试。', '', '', '', '', '', 'JIS T 8032-5:2015', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/MC3Je2ems6.pdf', '防护服-供化学防护的服装的测试方法-第5部分：液体喷雾的抗渗透性测定（人体模型喷雾试验）Protective Clothing - Test Methods For Clothing Providing Protection Against Chemicals - Part 5: Determination Of Resistance To Penetration By A Spray Of Liquid (manikin Spray Test)', '18', '', '', '', '');
INSERT INTO `subset` VALUES (137, 143, ' JIS T 8032-4:2015', '2015/11/1 0:00:00', '', '', 'Japanese Standards Association', '', '', 'Standard', '', '', '', '', '', '', '本标准主要修改采用了ISO 17491-4《防护服-防化学品防护服的试验方法-第4部分：用液体喷雾法测定抗渗透性（喷雾试验）》。本标准适用于以下两个不同级别的化学防护服（以下简称防护服），用于喷雾液体化学品，进而确定渗透性。 a） A方法：低级喷雾试验。 此测试针对覆盖整个身体表面的防护服。 当存在接触少量液体化学品或意外产生的少量飞地（飞溅）的风险时，应佩戴这种防护服。 b） B方法：高水平喷雾试验。 此测试，在防护服的不同部分之间的耐液体飞行（*）密封接头，并在适用的情况下，针对具有密封接头的防护服，以及与防护服同时使用的其他防护装置之间的间隙。 这种防护服覆盖整个身体表面，当暴露于液体喷雾颗粒时穿。', '', '', '', '', '', 'JIS T 8032-4:2015', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/XhNJPxeBcz.pdf', '防护服-提供化学防护的服装的测试方法-第4部分：液体喷雾的抗渗透性的测定（喷雾试验）Protective Clothing - Test Methods For Clothing Providing Protection Against Chemicals - Part 4: Determination Of Resistance To Penetration By A Spray Of Liquid (spray Test)', '20', '', '', '', '');
INSERT INTO `subset` VALUES (138, 144, ' JIS T 8031:2010', '2010/6/1 0:00:00', '', '', 'Japanese Standards Association', '', '', 'Standard', '', '', '', '', '', '', '本日本工业标准描述了一种实验室试验方法，该方法能够在液体连续接触和压力条件下，基于液体渗透的视觉检测，测定防护服中所用材料的可见渗透阻力。', '', '', '', '', '', 'JIS T 8031:2010', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/E2nhiNCYzz.pdf', '防液体化学品的防护服-在压力环境下的防护服材料对液体渗透的抵抗力的测定 Clothing for protection against liquid chemicals - Determination of the resistance of protective clothing materials to penetration by liquids under pressure', '21', 'JIS T 8031:2005 ', '', '', '');
INSERT INTO `subset` VALUES (139, 145, ' JIS T 8124-2:2008', '2008/9/1 0:00:00', '', '', 'Japanese Standards Association', '', '', 'Standard', '', '', '', '', '', '', '本标准规定了测定化学防护服对干燥细粉尘气溶胶的阻隔效率的试验方法。', '', '', '', '', '', 'JIS T 8124-2:2008', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/fzMNhAJY3i.pdf', '防固体颗粒的防护服-第2部分：确定细小颗粒的气溶胶向服内泄漏的测定方法 Protective clothing for use against solid particulates Part 2: Test method of determination of inward leakage of aerosols of fine particles into suits', '21', '', '', '', '');
INSERT INTO `subset` VALUES (140, 146, ' JIS T 8033:2008', '2008/9/1 0:00:00', '', '', 'Japanese Standards Association', '', '', 'Standard', '', '', '', '', '', '', '本标准规定了一种测量防护服材料对液体化学品（主要是低挥发性、低体积飞溅或低压喷射的化学品）的渗透、吸收和排斥指数的试验方法。', '', '', '', '', '', 'JIS T 8033:2008', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/zs8tHFSM2F.PDF', '防护服-防液体化学品-材料耐液体渗透的测试方法 Protective clothing - Protection against liquid chemicals - Test method for resistance of materials to penetration by liquids', '14', '', '', '', '');
