INSERT INTO `subset` VALUES (142, 148, ' AS/NZS 4011.2:2014', '2014/7/21 0:00:00', '', '', 'Standards Australia', '', '', 'Standard', '', '', '', '', '', '', '本标准指定用于医疗检查和诊断或治疗程序的包装无菌或散装非无菌（氯乙烯）手套的要求，以保护患者和用户免受交叉污染。它还包括聚（乙烯基）氯化物手套，用于处理受污染的医疗材料。', '', '', '', '', '', 'AS/NZS 4011.2:2014', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/11/7RcKRJ7Cm5.pdf', '一次性医疗检查手套 - 第2部分：聚氯乙烯手套规范 Single-use medical examination gloves- Part 2: Specification for gloves made from poly(vinyl chloride)', '16', 'DR AS/NZS 4011.2 ;AS/NZS 4011:1997 ;DR AS/NZS 4011.1 ;AS/NZS 4011:1997 ', '', '', 'ISO 11193-2:2006');
