INSERT INTO `subset` VALUES (188, 194, ' ISO 13982-1 : 2004(R2018)', '', '', '', 'International Organization for Standardization', '', '', 'Revision', '', '', '', '', '', '', '本标准规定了耐空气中固体颗粒渗透的化学防护服（5类）的最低要求。仅适用于空气中的固体颗粒物。', '', '', '', '', '', 'ISO 13982-1 : 2004(R2018)', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/cx7JBhd3mf.PDF', '固体颗粒用防护服-第1部分：对机体进行空气防护的化学防护服的防护性能（第5类）PROTECTIVE CLOTHING FOR USE AGAINST SOLID PARTICULATES - PART 1: PERFORMANCE REQUIREMENTS FOR CHEMICAL PROTECTIVE CLOTHING PROVIDING PROTECTION TO THE FULL BODY AGAINST AIRBORNE SOLID PARTICULATES (TYPE 5 CLOTHING)', '0', 'ISO/DIS 13982-1 : 60.00 (2004) ', '', '', 'PN EN ISO 13982-1 : 2008 AMD 1 2011 ;GOST R EN ISO 13982-1 : 2009 ;DS EN ISO 13982-1 : 2005 AMD 1 2010 ');
