INSERT INTO `subset` VALUES (190, 196, ' ISO 16603:2004', '2004/3/30 0:00:00', '', '', 'International Organization for Standardization', '', '', 'Standard', '', '', '', '', '', '', '本国际标准描述了一种实验室测试方法，用于测量服装材料对血液和体液的渗透性。此测试方法使用 ISO 13994 测试装置在指定条件下与材料试样连续接触的合成血液。不过，这种测试方法在测试具有厚内衬的、容易吸收合成血液的防护服材料方面并不总是有效的。', '', '', '', '', '', 'ISO 16603:2004', '现行', 'http://styuanwen.oss-cn-zhangjiakou.aliyuncs.com/2020/02/09/n7bRRzFNaR.PDF', '阻隔血液和体液接触的衣服。防护服材料对血液和体液渗透的抵抗力的测定。使用合成血液的试验方法 Clothing for protection against contact with blood and body fluids - Determination of the resistance of protective clothing materials to penetration by blood and body fluids - Test method using synthetic blood', '11', 'ISO/DIS 16603 ', '', '', 'NEN ISO 16603:2004;BS ISO 16603:2004;KS K ISO 16603:2012;KS K ISO 16603:2007');
